echo -e "        .            .   . ${YELLOW}*${RESET}    .   .  ${GREEN}\`${RESET} ${GREEN}|${RESET}${GREEN}|${RESET}${GREEN}|${RESET}.  +        + ${GREEN}'${RESET}${GREEN}|${RESET} ${GREEN}|${RESET}${GREEN}|${RESET}${GREEN}|${RESET}  .  ${GREEN}|${RESET}${GREEN}|${RESET}${GREEN}\`${RESET}"
