echo -e "              +         .      .    ${GREEN}|${RESET} .      ${GREEN}\`${RESET}${GREEN}|${RESET}${GREEN}|${RESET}${GREEN}|${RESET}.:      .${GREEN}|${RESET}${GREEN}|${RESET}    .      .    ${GREEN}\`${RESET}"
